import React,  {useReducer } from "react";
import DigitButton from "./DigitButton";
import OperationButton from "./OperationButton"
import "./styles.css"

export const ACTIONS = {
  ADD_DIGIT: 'add-digit',
  CHOOSE_OPERATION: 'choose-operation',
  CLEAR_DIGIT: 'clear-digit',
  DELETE_DIGIT: 'delete_digit',
  EVALUATE_DIGIT: 'evaluate_digit'
}

function reducer(state, {type, payload}){
  switch(type){
    case ACTIONS.ADD_DIGIT:
      if (state.overwrite){
        return{
          ...state,
          currentOperand: payload.digit,
          overwrite:false,
        }
      }
      if(payload.digit === "0" && state.currentOperand === "0") {
        return state;
      }
      if(payload.digit === "." && state.currentOperand.includes (".")) {
        return state;
      }
      return {
        ...state,
        currentOperand: `${state.currentOperand || ""}${payload.digit}`,
      }
    case ACTIONS.CHOOSE_OPERATION:
      if(state.currentOperand == null && state.previousOperand == null){
        return state;
      }

      if(state.currentOperand == null) {
        return {
          ...state,
          operation:payload.operation,
        }
      }

      if(state.previousOperand == null){
        return {
          ...state,
          operation: payload.operation,
          previousOperand: state.currentOperand,
          currentOperand: null,
        };
      }

      return {
        ...state,
        previousOperand: evaluate(state.currentOperand,state.previousOperand,state.operation),
        operation: payload.operation,
        currentOperand: null,
      }

    case ACTIONS.DELETE_DIGIT:
      if (state.overwrite){
        return {
          ...state,
          overwrite: false,
          currentOperand: null,
        }
      }

      if(state.currentOperand == null){
        return state;
      }

      if(state.currentOperand.length === 1 ){
        return {
          ...state,
          currentOperand: null
        }
      }

      return {
        ...state,
        currentOperand: state.currentOperand.slice(0,-1)
      }

    case ACTIONS.EVALUATE_DIGIT:
      if (state.operation == null || state.currentOperand == null || state.previousOperand ==null){
        return state;
      }

      return {
        ...state,
        overwrite:true,
        currentOperand: evaluate(state.currentOperand,state.previousOperand,state.operation),
        previousOperand:null,
        operation:null,
      }

    case ACTIONS.CLEAR_DIGIT:
      return {}

    default:
      return {}
  }
}

function evaluate(currentOperand, previousOperand, operation){
  const prev = parseFloat(previousOperand);
  const curr = parseFloat(currentOperand);
  if (isNaN(prev) || isNaN(curr)) return "";
  let computation = '';
  switch (operation){
    case "+":
      computation = prev + curr;
      break;

    case "-":
      computation = prev - curr;
      break;

    case "x":
      computation = prev * curr;
      break;

    case "รท":
      computation = prev / curr;
      break;

    default:
      computation = curr;
  }
  return computation.toString();
}

const INTEGER_FORMATTER = new Intl.NumberFormat("en-us",{ maximumFractionDigits: 0,});

function formatOperand(operand){
  if (operand == null) return; 
    const [integer, decimal] = operand.split('.');
  if(decimal == null ){
    return INTEGER_FORMATTER.format(integer);

  }
}

function App() {
  const [{currentOperand, previousOperand, operation}, dispatch] = useReducer(reducer,{})

  return (
    <div className="calculator-grid">
      <div className="output"> 
        <div className="previous-operand">{formatOperand(previousOperand)} {operation} </div>
        <div className="current-operand">{formatOperand(currentOperand)}</div>
      </div>
      <button onClick={()=>dispatch({type: ACTIONS.CLEAR_DIGIT})}>AC</button>
      <button onClick={()=>dispatch({type: ACTIONS.DELETE_DIGIT})}>DEL</button>
      <button>+/-</button>
      <OperationButton operation = "รท" dispatch={dispatch} />
      <DigitButton digit = "7" dispatch={dispatch} />
      <DigitButton digit = "8" dispatch={dispatch} />
      <DigitButton digit = "9" dispatch={dispatch} />
      <OperationButton operation = "x" dispatch={dispatch} />
      <DigitButton digit = "4" dispatch={dispatch} />
      <DigitButton digit = "5" dispatch={dispatch} />
      <DigitButton digit = "6" dispatch={dispatch} />
      <OperationButton operation = "-" dispatch={dispatch} />
      <DigitButton digit = "1" dispatch={dispatch} />
      <DigitButton digit = "2" dispatch={dispatch} />
      <DigitButton digit = "3" dispatch={dispatch} />
      <OperationButton operation= "+" dispatch={dispatch} />
      <DigitButton digit = "0" dispatch={dispatch} />
      <DigitButton digit = "." dispatch={dispatch} />
      <button onClick={() =>dispatch( {type: ACTIONS.EVALUATE_DIGIT})} className="span-two">=</button>

    </div>
  )
}

export default App;
